#!/bin/bash
echo
echo " [ $(basename $BASH_SOURCE) ] "
shasum -a 256 *
shasum -a 256 --check shasum256.txt

TMUX_SS="$(date +%b%m%H%M%S)"
EC_KY=$(cat << 'EOF'

EOF
)
eval "$(ssh-agent -s)"
AGENT_PID=$!
trap 'ssh-agent -k > /dev/null; echo "ssh-agent killed";' EXIT

echo -n "DEC: (+1+1+1)"
read -s PASD
echo

if [ "$1" == "init" ]; then
mkdir -p .ssshhh
openssl aes-256-cbc -d -salt -pass pass:"$PASD" -in <(echo "$EC_KY" | base64 --decode) -out .ssshhh/l
unset PASD
chmod 600 .ssshhh/l
tee Makefile <<'EOF'
l:
	@ssh  -t -i .ssshhh/l -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null "l@MacBook-Pro.local" "tmux attach || tmux"
.PHONY: l
git:
	git rebase -i  --root main
	GIT_AUTHOR_NAME="noname" GIT_AUTHOR_EMAIL="noemail" GIT_COMMITTER_NAME="noname" GIT_COMMITTER_EMAIL="noemail" git commit --amend --no-edit --reset-author
.PHONY: git
clean:
	rm -rf .ssshhh Makefile
.PHONY: clean
EOF
exit 0
fi

openssl aes-256-cbc -d -salt -pass pass:"$PASD" -in <(echo "$EC_KY" | base64 --decode) | ssh-add -
unset PASD

if [ "$1" == "ssh" ]; then
ssh -t -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null "l@MacBook-Pro.local" "tmux attach || tmux"
exit 0
fi

rsync -avz --chmod=u+rwx,g-rwx,o-rwx -e "ssh -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null" z*.sh l@MacBook-Pro.local:/home/l/

if [ "$1" == "rsync" ]; then
exit 0
fi

ssh -t -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null "l@MacBook-Pro.local" << EOF
tmux new-session -d -s "$TMUX_SS"
tmux send-keys -t "$TMUX_SS" "./z1-env-setup.sh && ./z2-config-setup.sh && ./z3-docker-setup.sh && ./z4-network-setup.sh && ./z5-app-setup.sh " C-m
EOF

echo
echo "Job continued in tmux session: $TMUX_SS "
echo

ssh  -t -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null "l@MacBook-Pro.local" "tmux attach"

